import React from 'react'
import { useState } from 'react'
import '/Users/nikitamandlik/Desktop/weather-app/weather-app/src/index.css'
function InputCity({onInputHandler,cityName,onSubmitHandler}){

    return(
        <div className='input-box'>

        <input
          placeholder='Enter City Name'
          type="text" 
          value={cityName}
          onChange = {onInputHandler}
        /><br></br>
        <button className='submit-button' type = 'submit' onClick={onSubmitHandler} >Search City</button>


        </div>
    )
}
export default InputCity;
